import React from 'react'

import ExploreTogetherGIF from './assets/ExploreTogether.gif'
import CraftingIdeasGIF from './assets/CraftingIdeas.gif'
import BuildAndColaborateGIF from './assets/Build&Collaborate.gif'
import TestAndImproveGIF from './assets/Test&Improve.gif'

import './styles/Cards.css'

const Cards: React.FC<{ activeCard: number }> = ({ activeCard }) => {
  const stepsCards = [
    { icon: <svg width="72" height="72" viewBox="0 0 72 72" fill="none" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink"> <rect width="72" height="72" fill="url(#pattern0_970_981)"/> <defs> <pattern id="pattern0_970_981" patternContentUnits="objectBoundingBox" width="1" height="1"> <use xlinkHref="#image0_970_981" transform="scale(0.0025)"/> </pattern> <image id="image0_970_981" width="400" height="400" xlinkHref="data:image/png;base64,"/> </defs> </svg>,
      gif: <img src={ExploreTogetherGIF} alt="Explore together gif" />,
      title: 'Explore together',
      isActive: activeCard === 0
    },
    { icon: <svg width="72" height="72" viewBox="0 0 72 72" fill="none" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink"> <rect width="72" height="72" fill="url(#pattern0_970_985)"/> <defs> <pattern id="pattern0_970_985" patternContentUnits="objectBoundingBox" width="1" height="1"> <use xlinkHref="#image0_970_985" transform="scale(0.0025)"/> </pattern> <image id="image0_970_985" width="400" height="400" xlinkHref="data:image/png;base64,"/> </defs> </svg>,
      gif: <img src={CraftingIdeasGIF} alt="Crafting ideas gif" />,
      title: 'Crafting ideas',
      isActive: activeCard === 1
    },
    { icon: <svg width="72" height="72" viewBox="0 0 72 72" fill="none" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink"> <rect width="72" height="72" fill="url(#pattern0_970_983)"/> <defs> <pattern id="pattern0_970_983" patternContentUnits="objectBoundingBox" width="1" height="1"> <use xlinkHref="#image0_970_983" transform="scale(0.0025)"/></pattern> <image id="image0_970_983" width="400" height="400" xlinkHref="data:image/png;base64,"/> </defs> </svg>,
      gif: <img src={BuildAndColaborateGIF} alt="Build & Collaborate gif" />,
      title: 'Build & Collaborate',
      isActive: activeCard === 2
    },
    { icon: <svg width="72" height="72" viewBox="0 0 72 72" fill="none" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink"> <rect width="72" height="72" fill="url(#pattern0_970_979)"/> <defs> <pattern id="pattern0_970_979" patternContentUnits="objectBoundingBox" width="1" height="1"> <use xlinkHref="#image0_970_979" transform="scale(0.0025)"/> </pattern> <image id="image0_970_979" width="400" height="400" xlinkHref="data:image/png;base64,"/> </defs> </svg>,
      gif: <img src={TestAndImproveGIF} alt="Test & Improve gif" />,
      title: 'Test & Improve',
      isActive: activeCard === 3
    }
  ]

  return (
    <section className='stepsCards'>
      {stepsCards.map((step) => (
        <Card key={step.title} icon={step.icon} gif={step.gif} title={step.title} isActive={step.isActive} />
      ))}
    </section>
  )
}

interface CardProps {
  icon: React.ReactNode,
  gif: React.ReactNode,
  title: string,
  isActive: boolean
}

const Card: React.FC<CardProps> = ({ icon, gif, title, isActive }) => {
  return (
    <section className='card'>
      {isActive ? gif : icon}
      <p> {title} </p>
    </section>
  )
}

export default Cards